import React, { useState } from 'react';
import Login from './components/Login';
import BetForm from './components/BetForm';
import Navbar from './components/Navbar';

const App = () => {
  const [isLoggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  const handleBet = ({ amount, selectedCrypto, prediction, date }) => {
    // Diğer dosyalarla bağlantı kurmadan sadece form bilgilerini kullanabilirsiniz
    const asset = 0; // Bitcoin için
    const user = '0xabc'; // Gerçek bir kullanıcı adresi olmalı
    // createBet ve getBetResult fonksiyonlarına dokunmamışsınız, onları gerekli şekilde güncelleyin
    // createBet(user, asset, Number(amount), prediction, new Date(date).getTime());
    // const result = getBetResult(user, new Date(date).getTime());
    // console.log('Bahis Sonucu:', result ? 'Kazandınız!' : 'Kaybettiniz.');
    alert('Bahis yapıldı!');
  };

  return (
    <div>
      {isLoggedIn && <Navbar onLogout={handleLogout} />}
      {!isLoggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <Navbar onLogout={handleLogout} />
          <BetForm onBet={handleBet} />
        </>
      )}
    </div>
  );
};

export default App;
