// src/App.js

import React, { useState } from 'react';
import Login from './components/Login';
import BetForm from './components/BetForm';

const createBet = (address, amount, prediction, date) => {
  // Burada akıllı sözleşme ile etkileşim yapılacak simülasyonu yapabilirsiniz
  console.log('Bahis Yapıldı:', { address, amount, prediction, date });
  // Akıllı sözleşme ile gerçek etkileşim burada olmalıdır
  // Örneğin: BetMoveContract.BetModule.createBet(address, amount, prediction, date);
};

module.exports = { createBet };
const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleBetSubmit = (betInfo) => {
    createBet('0xabc', betInfo.amount, betInfo.prediction, betInfo.date);
  };

  return (
    <div>
      {!loggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <h1>Bitcoin & Ethereum Bahis DApp</h1>
          <BetForm onSubmit={handleBetSubmit} />
        </>
      )}
    </div>
  );
};

export default App;
