// src/components/BetForm.js

import React, { useState } from 'react';

const BetForm = ({ onSubmit }) => {
  const [amount, setAmount] = useState('');
  const [prediction, setPrediction] = useState(true);
  const [betDate, setBetDate] = useState('');

  const handleSubmit = () => {
    const date = new Date(betDate).getTime(); // Tarih bilgisini timestamp'e çevir

    if (!isNaN(date)) {
      onSubmit({ amount, prediction, date });
      alert('Bahis başarılı!'); // Popup uyarı
    } else {
      alert('Geçersiz tarih!');
    }
  };

  return (
    <div>
      <h2>Bahis Yap</h2>
      <label>
        Miktar:
        <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} />
      </label>
      <label>
        Tahmin:
        <select value={prediction} onChange={(e) => setPrediction(e.target.value === 'true')}>
          <option value={true}>Yükseliş</option>
          <option value={false}>Düşüş</option>
        </select>
      </label>
      <label>
        Bahis Tarihi:
        <input type="date" value={betDate} onChange={(e) => setBetDate(e.target.value)} />
      </label>
      <button onClick={handleSubmit}>Bahis Yap</button>
    </div>
  );
};

export default BetForm;
