// src/components/Navbar.js

import React, { useState } from 'react';
import { Navbar, Nav, Modal, Button, Form } from 'react-bootstrap';

const CustomNavbar = ({ onLogout, onShowBetForm }) => {
  const [showPasswordReset, setShowPasswordReset] = useState(false);

  const handleShowPasswordReset = () => {
    setShowPasswordReset(true);
  };

  return (
    <Navbar bg="dark" variant="dark">
      <Navbar.Brand href="#">AleaRise</Navbar.Brand>
      <Nav className="ml-auto">
        <Nav.Link onClick={onShowBetForm}>Bahis</Nav.Link>
        <Nav.Link onClick={handleShowPasswordReset}>Şifremi Unuttum</Nav.Link>
        <Nav.Link onClick={onLogout}>Çıkış</Nav.Link>
      </Nav>

      {/* Şifre Değiştirme Modal */}
      <Modal show={showPasswordReset} onHide={() => setShowPasswordReset(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Şifre Değiştirme</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <PasswordResetForm onClose={() => setShowPasswordReset(false)} />
        </Modal.Body>
      </Modal>
    </Navbar>
  );
};

export default CustomNavbar;
