// src/components/BetForm.js

import React, { useState } from 'react';

const BetForm = ({ onBet }) => {
  const [amount, setAmount] = useState('');
  const [currency, setCurrency] = useState('USD'); // Varsayılan olarak dolar
  const [prediction, setPrediction] = useState(true); // Varsayılan olarak yükseliş
  const [date, setDate] = useState('');

  const handleBet = () => {
    if (amount && date) {
      // Diğer dosyalarla bağlantı kurmadan sadece form bilgilerini iletiyoruz
      onBet({ amount, currency, prediction, date });
      alert('Bahis yapıldı!');
    } else {
      alert('Lütfen miktar ve tarih girin.');
    }
  };

  return (
    <div>
      <h2>Bahis Yap</h2>
      <label>Miktar: 
        <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} />
      </label>
      <br />
      <label>Para Birimi: 
        <select value={currency} onChange={(e) => setCurrency(e.target.value)}>
          <option value="USD">Dolar</option>
          <option value="EUR">Euro</option>
          <option value="LTC">Litecoin</option>
          <option value="SUI">SUI</option>
        </select>
      </label>
      <br />
      <label>Tahmin: 
        <select value={prediction} onChange={(e) => setPrediction(e.target.value === 'true')}>
          <option value={true}>Yükseliş</option>
          <option value={false}>Düşüş</option>
        </select>
      </label>
      <br />
      <label>Tarih: 
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      </label>
      <br />
      <button onClick={handleBet}>Bahis Yap</button>
    </div>
  );
};

export default BetForm;
